import { extendTheme } from "native-base";
export const theme = extendTheme({
  //  breakpoints: {
  //     base: 0,
  //     sm: 540,
  //     md: 1024,
  //     lg: 3000,
  //   },
  colors: {
    bgColorLight: "#F2F4FA",
    bgColorDark: "#212529",
    textColorDark: "#0e0e0e",
    textColorLight: "#8894a0",
    blueDark: "#2988f9",
    blueLight: "#87bcfb",
    blueSuperLight: "#b4d6f6",
  },
  components: {
    Heading: {
      baseStyle: {
        color: "custom.100",
      },
    },
  },
});
